package milliwatt.phuc.wrapper;

public class ThucHanhToanHoc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.sqrt(4.6));
		System.out.println(Math.PI);
		System.out.println(Math.abs(-189));
		
		System.out.println(Math.random()); // ngau nhien tu 0-1
		System.out.println(Math.ceil(7.6)); // lam tron len
		System.out.println(Math.floor(7.6)); // lam tron xuong 
		
		System.out.println(Math.cos(90)); // radian
		System.out.println(Math.max(4, 5));
	}

}
